import React, { Component } from "react";
import Sidebar from "./components/SideBar";
import { Appointments } from "./components/Appointments";
import "./App.css";

class App extends Component {
  state = {
    physicians: [],
    currentPhysician: {},
    appointments: []
  };
  componentDidMount = () => {
    this.callApi()
      .then(resp => {
        this.setState({ physicians: resp.physicians });
      })
      .catch(err => console.error(err));
  };
  callApi = async () => {
    const response = await fetch("/api/physicians");
    const body = await response.json();
    if (response.status !== 200) throw Error(body.message);
    return body;
  };
  getAppointments = async physician => {
    const response = await fetch("/api/appointments", {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify({ physicianId: physician.id })
    });
    const appointments = await response.text();
    this.setState({ appointments: appointments });
    return appointments;
  };
  setPhysician = physician => {
    this.setState();
  };
  render() {
    const {
      physicians = [],
      currentPhysician = {},
      appointments = []
    } = this.state;
    return (
      <div className="App">
        <Sidebar physicians={physicians} current={currentPhysician} />
        <Appointments
          physician={currentPhysician}
          appointments={appointments}
        />
      </div>
    );
  }
}

export default App;
