import React, { Component } from "react";
import "./Appointments.css";

export class Appointments extends Component {
  state = {
    appointments: []
  };
  shouldComponentUpdate = ({ physician: { id } }) => {
    const {
      physician: { id: oldId }
    } = this.props;
    return id !== oldId && id !== "";
  };
  componentDidUpdate = () => {
    const {
      physician: { id }
    } = this.props;
    console.log(id);
    this.getAppointments(id).catch(err => console.error(err));
  };

  getAppointments = async id => {
    const { getAppointments } = this.props;
    const currAppointments = await getAppointments(id).currentAppointments;
    console.log(currAppointments);
    this.setState({ appointments: currAppointments });
  };

  render() {
    const { physician } = this.props;
    const { appointments = [] } = this.state;
    console.log("APPO", appointments);
    return (
      <div className="Appointments">
        {physician.id ? (
          <div>
            <div>
              Dr. {physician.firstName} {physician.lastName}
            </div>
            <div>{physician.email}</div>
            {appointments.length > 0 && (
              <ul>
                {appointments.map((curr, i) => (
                  <li key={i + 1}>
                    {curr.patient} {curr.time} {curr.kind}
                  </li>
                ))}
              </ul>
            )}
          </div>
        ) : (
          <div>Please Select a Physician</div>
        )}
      </div>
    );
  }
}

export default Appointments;
