import React, { Component } from "react";
import { Sidebar } from "./components/SideBar";
import { Appointments } from "./components/Appointments";
import "./App.css";

class App extends Component {
  state = {
    physicians: "",
    currentPhysician: null,
    appointments: []
  };
  componentDidMount = () => {
    this.callApi()
      .then(resp => {
        this.setState({ physicians: resp.physicians });
      })
      .catch(err => console.error(err));
  };
  callApi = async () => {
    const response = await fetch("/api/physicians");
    const body = await response.json();
    if (response.status !== 200) throw Error(body.message);
    return body;
  };
  handleSubmit = async e => {
    e.preventDefault();
    const response = await fetch("/api/appointments", {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify({ physicianId: this.state.currentPhysician.id })
    });
    const body = await response.text();
    this.setState({ appointments: body });
  };
  render() {
    const { physicians, currentPhysician, appointments = [] } = this.state;
    console.log(physicians);
    return (
      <div className="App">
        <Sidebar physicians={physicians} current={currentPhysician} />
        <Appointments
          physician={currentPhysician}
          appointments={appointments}
        />
      </div>
    );
  }
}

export default App;
